/****************************************************
 * MQTT MSG PREPROCESSOR                            *
 *                                                  *
 * REVD MSG FORMAT GUIDE:                           *
 *                                                  *
 * Topic:   users/$user_name/$client_id/$sub_topic  *
 * Payload: $payload                                *
 *                                                  *
 * PREPROCESSED RESULT:                             *
 * msg.mqtt: {                                      *
 *      topic:      $full_topic,                    *
 *      uname:      $user_name,                     *
 *      cid:        $client_id,                     *
 *      subTopic:   $sub_topic,                     *
 *      payload:    $payload,                       *
 *      timestamp:  current timestamp               *
 * }                                                *
 ****************************************************/

// MQTT MSG Object
msg.mqtt = {};

// Full Topic
msg.mqtt.topic = escape(msg.topic);

const parsed = msg.topic.split('/');

// User_ID
msg.mqtt.uname = escape(parsed[1]);

// Client_ID
msg.mqtt.cid = escape(parsed[2]);

// Sub Topic
msg.mqtt.subTopic = escape(parsed[3]);

// Payload
msg.mqtt.payload = escape(msg.payload);

// Timestamp
// get microtime
let timestamp = new Date().getTime()/1000;
// pad it with trailing zeroes
timestamp = timestamp.toString() + "000";
// trim to exact length 10 + 1 + 3
msg.mqtt.timestamp = timestamp.substring(0, 14);

return msg;

// Call webhook
if(msg.payload.length !== 0)
{
    // Prepare values
    const topic = encodeURIComponent(msg.mqtt.topic);
    const uname = encodeURIComponent(msg.mqtt.uname);
    const cid = encodeURIComponent(msg.mqtt.cid);
    const subTopic = encodeURIComponent(msg.mqtt.subTopic);
    const payload = encodeURIComponent(msg.mqtt.payload);
    const timestamp = encodeURIComponent(msg.mqtt.timestamp);

    // Send webHook
    for(var i = 0; i < msg.payload.length; i++)
    {
        msg.method = msg.payload[i].method;
        msg.url = msg.payload[i].webHook
            .replace(/yytopic/g, topic)
            .replace(/yyuname/g, uname)
            .replace(/yycid/g, cid)
            .replace(/yysubtopic/g, subTopic)
            .replace(/yypayload/g, payload)
            .replace(/yytimestamp/g, timestamp);
        node.send([msg]);
    }
}

